    private void btnRegistrarseActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegistrarseActionPerformed
        //Aqui se configura el boton de registrarse, junto con todos los textos y verificaciones
        SqlUsuarios modSql = new SqlUsuarios();
        Usuarios mod = new Usuarios();
        String pass = new String(txtPassword.getPassword());
        String passCon = new String(txtConfirmaPassword.getPassword());

        if (txtUsuario.getText().equals("") || pass.equals("") || passCon.equals("") || txtNombre.getText().equals("") || txtApellido.getText().equals("")
                || txtCorreo.getText().equals("") || txtTelefono.getText().equals("")) {

            JOptionPane.showMessageDialog(null, "Hay campos vacios, debe de llenar todos los campos");

        } else {

            if (pass.equals(passCon)) {

                if (modSql.existeUsuario(txtUsuario.getText()) == 0) {

                    if (modSql.esEmail(txtCorreo.getText())) {

                        String nuevopass = hash.sha1(pass);

                        mod.setUsuario(txtUsuario.getText());
                        mod.setContrase単a(nuevopass);
                        mod.setNombre(txtNombre.getText());
                        mod.setApellido(txtApellido.getText());
                        mod.setCorreo(txtCorreo.getText());
                        mod.setTelefono(txtTelefono.getText());

                        if (modSql.registrar(mod)) {
                            JOptionPane.showMessageDialog(null, "Registrado");
                            limpiar();
                        } else {
                            JOptionPane.showMessageDialog(null, "Error al guardar");
                        }
                    } else {
                        JOptionPane.showMessageDialog(null, "El correo electronico no es valido");
                    }
                } else {
                    JOptionPane.showMessageDialog(null, "El usuario ya existe, si tiene una cuenta inicie sesion");
                }
            } else {
                JOptionPane.showMessageDialog(null, "Las contrase単a no coincide");
            }

        }
    }//GEN-LAST:event_btnRegistrarseActionPerformed

